class Solution {
public:
    int maxOperations(string s) {

        int count=0;

        int result=0;

        for(int i=s.length()-1;i>=0;i--)
        {
            if(s[i] == '0')
            {
                if(s[i] != s[i+1])
                    count++;
            }
            else
            {
                result += count;
            }

        }

        return result;

    }
};
