class Solution(object):
    def maxOperations(self, s):
        """
        :type s: str
        :rtype: int
        """
        ones = 0
        shifts = 0
        for i in range(len(s)-1):
            if s[i]=='1' and s[i+1]=='0':
                ones += 1
                shifts += ones
            if s[i]=='1' and s[i+1]=='1':
                ones += 1
        return shifts
