class Solution {
public:
    int maxOperations(string s) {
        int l = s.length();
        vector<int> cnt(l + 6, 0);

        int pre = 0;
        for (int i = 0; i < l; i++) {
            pre += (s[i] == '1');
            cnt[i] = pre;
        }
        int ans = 0;
        for (int i = l - 2; i >= 0; i--) {
            if (s[i] == '1' && s[i + 1] == '0') {
                ans += cnt[i];
            }
        }
        return ans;
    }
};
