class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, tmp = 0, idx = 0;
        while(idx < s.size()){
            if(s[idx] == '1'){
                tmp++;
                if(idx+1 < s.size() && s[idx+1] != '1'){
                    while(idx+1 < s.size() && s[idx+1] != '1') idx++;
                    ans += tmp;
                }
            }
            idx++;
        }
        return ans;
    }
};
