class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        int cnt = 0;
        int idx = 0;
        int ans = 0;
        while(idx < n) {
            while(idx < n && s.charAt(idx) == '0') idx++;
            if(idx == n) {
                continue;
            }
            while(idx < n && s.charAt(idx) == '1') {
                idx++;
                cnt++;
            }
            if(idx != n) {
                ans += cnt;
            }
        }
        return ans;
    }
}
