class Solution {
    public int maxOperations(String s) {
        int cnt = 0;
        int n = s.length();
        int i = 0;
        int ans = 0;
        boolean met = false;
        while (i < n)   {
            char c = s.charAt(i);
            if (c == '1')   {
                int j = i;
                while (j < n && s.charAt(j) == '1')
                    j++;
                cnt += (j - i);
                met = true;
                i = j;    
            }
            else    {
                i++;
                if (!met)
                    continue;
                met = false;
                ans += cnt;
            }
        }

        return ans;
    }
}


