class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();

        int curr = 0;

        int ans = 0;
        for(int i = 0; i<n; i++) {
            if(s[i] == '1') {
                if(i > 0 && s[i-1] != '1') {
                    ans += curr;
                }

                curr++;
            }
        }

        if(s[n-1] == '0')
            ans += curr;

        return ans;
    }
};
