class Solution(object):
    def maxOperations(self, s):
        """
        :type s: str
        :rtype: int
        """
        n = len(s)
        ans = 0
        cnt = 0
        flag = False
        for i in range(n):
            if s[i] == '1':
                cnt += 1
                flag = False
            else:
                if not flag:
                    ans += cnt
                    flag = True
        return ans
