class Solution {
public:
    int maxOperations(string s) {
        int ret = 0;
        int n = s.size();
        int l = -1, r = -1;
        for(int i = 0;i<n;i++){
            if(s[i]=='0')continue;
            if(l==-1){
                l = r = i;
                continue;
            }
            if(r==i-1){
                r = i;
                continue;
            }
            ret+=(r-l+1);
            int tt = r-l+1;
            r = i;
            l = i-tt;
        }
        if(r!=-1&&r!=n-1){
            ret+=(r-l+1);
        }
        return ret;
    }
};
