class Solution {
public:
    int maxOperations(string s) {
        int n=s.length(),i,prev=n-1,ans=0,pc=0,i0=0;
        for(i=n-1;i>=0;i--){
            if(s[i]=='1'){
                if(i0==1){
                    pc++;
                }
                ans+=pc;
                i0=0;
            }
            else{
              i0=1;  
            } 

        }
        return ans;
    }
};
