class Solution:
    def maxOperations(self, s: str) -> int:

        n = len(s)
        dp = [0] * n
        cur = 0
        ans = 0
        flag = 0
        for i, c in enumerate(s):
            if c == '1':
                cur += 1
                flag = 1
            elif flag:
                ans += cur
                flag = 0
        return ans
