using ll = long long;
using vi = vector<ll>;
using vvi = vector<vi>;
using pii = pair<ll,ll>;
using vp = vector<pii>;
using ti3 = tuple<ll,ll,ll>;
using vs = vector<string>;

template<typename T>
using min_pq = priority_queue<T, vector<T>, greater<T>>;
template<typename T>
using max_pq = priority_queue<T>;

class Solution {
public:
    int maxOperations(string s) {
        vector<int> a;
        for (auto c : s) {
            if (c == '1') a.push_back(1);
            else {
                if (a.size() && a.back() == 1) a.push_back(0);
            }
        }
        // debug(a);
        int n = a.size();
        vi b(n);
        for (int i=0; i<n; i++) {
            b[i] = a[i] ^ 1;
        }
        vi suf(n+1);
        for (int i=n-1; i>=0; i--) {
            suf[i] = suf[i+1] + b[i];
        }
        ll ans = 0;
        for (int i=0; i<n; i++) {
            if (a[i]) ans += suf[i];
        }
        return ans;
    }
};
