class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        long long ans=0;
        int ones=0;
        for(int i=0;i<n;i++){
           ones+=s[i]=='1';
           if(s[i]=='1')continue;
           int j=i;
           while(j<n and s[j]=='0')j++;
           ans+=ones;
           i=j-1;
        }
        return ans;
    }
};
