class Solution {
public:
    int maxOperations(string s) {
        int cnt1=0;
        int ans=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='0'){
                if(i==s.size()-1||s[i+1]=='1'){
                    ans+=cnt1;
                }
            }
            else{
                cnt1++;
            }
        }
        return ans;
    }
};
