class Solution:
    def maxOperations(self, s: str) -> int:
        gps = []
        curr = 0
        s += "0"
        for i in s:
            if i == "1":
                curr += 1
            elif curr != 0:
                gps.append(curr)
                curr = 0
        s = s[:-1]
        x = 0
        times = len(gps) - 1
        print(gps)
        for i in gps[:-1]:
            x += times*i
            times -= 1
        if s[-1] == "0":
            x += sum(gps)
        return x
