class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, i, n = s.size(), d, e = 0;
        for (i = 0; i < n-1; i++) {
            if (s[i] == '1')
                e++;
            else if (s[i + 1] == '1')
                ans += e;
        }
        if(s.back()=='0')ans+=e;
        return ans;
    }
};
