class Solution {
public:
  int maxOperations(string s) {
    int l = s.size(), a = 0; vector<int> d;
    for(int i =0; i<l; ) if(s[l-1-i] == '1'){
      ++a; ++i;
    } else {
      d.push_back(a); a =0;
      while(i<l && s[l-1-i] == '0') ++i;
    }
    if(a) d.push_back(a);
    int as = 0;
    for(int i =1; i<d.size(); ++i) {
      // printf("%d %d\n", i, d[i]);
      as += i*d[i];
    }
    return as;
  }
};
