class Solution {
    public int maxOperations(String s) {
        s += "1";
        int cnt = 0, re = 0;
        boolean b = false;
        for(char c : s.toCharArray()) {
            if (c == '1') {
                if (b) re += cnt;
                b = false;
                cnt++;
            } else b = true;
        }
        return re;
    }
}
