class Solution {
public:
    int maxOperations(string s) {
        int c1 = 0;
        int ans = 0;
        if(s[0] == '1') c1++;
        for(int i = 1; i < s.length(); i++){
            if(s[i] == '0' && s[i-1] != '0'){
                ans += c1;
            }
            if(s[i] == '1'){
                c1++;
            }
        }
        return ans;
    }
};
