class Solution {
public:
    int maxOperations(string s) {
        int cnt0 = 0;
        int cnt1 = 0;
        char prev = '1';
        int res = 0;
        int n = s.length();
        for(int i = n - 1; i >= 0; i--){
            if(s[i] == '0'){
                if(prev == '1'){
                    cnt0++;
                }
            }
            else{
                res += cnt0;
            }
            prev = s[i];
        }
        return res;
    }
};
