class Solution:
    def maxOperations(self, s: str) -> int:
        # 找规律
        n = len(s)
        res = 0
        pre = 0
        for i in range(n - 1, -1, -1):
            if s[i] == '1':
                if i < n - 1 and s[i + 1] == '0':
                    pre += 1
                res += pre
        return res
