class Solution {
public:
    int maxOperations(string s) {
        int ans=0,cnt=0,p=0;
        for(int i=s.size()-1;i>-1;i--)
        {
            if(s[i]=='0')
            {
                p=1;
            }
            else
            {
                if(p)
                {
                    cnt++;
                }
                ans+=cnt;
                p=0;
            }
        }
        return ans;
    }
};
