class Solution {
  // seems like a greedy
  // move 1s from left to right
  // 
  public int maxOperations(String _s) {
    char[] s = _s.toCharArray();
    int n = s.length;

    int cnt = 0, res = 0;
    boolean added = false;
    for (int i = 0; i < n; i++) {
      if (s[i] == '1') {
        cnt++;
        added = false;
      }
      else if (!added) {
        res += cnt;
        added = true;
      }
    }

    return res;
  }
}
