class Solution {
public:
    int maxOperations(string s) {
        int n = s.length(), one = 0, zero = 0, ans = 0;
        for (int i=0; i<n; i++) {
            if (s[i] == '1') {
                one++;
            } else {
                while (i < n && s[i] == '0') {
                    i++;
                }
                i--;
                ans += one;
            }
        }
        return ans;
    }
};
