class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int ans=0;
        int count=0;
        int ind=n-2;
        while(ind>=0){
            if(s[ind]=='0') {
                ind--;
                continue;
            }
            int onecount=0;
            int j=ind;
            while(j>=0 && s[j]=='1'){
                onecount++;
                j--;
            }
            if(count==0){
                if(s[ind+1]=='0'){
                    ans+=(onecount)*(count+1);
                    count++;
                }
            }else{
                ans+=(onecount)*(count+1);
                count++;
            }
            ind=j;
        }
        return ans;
    }
};
