class Solution {
public:
    int maxOperations(string s) {
        vector<int> vec(s.size(),0);
        int ans=0;
        for(int i=s.size()-2;i>=0;i--){
            if(s[i]=='1' && s[i+1]=='0') vec[i]=1;
            vec[i]+=vec[i+1];
            if(s[i]=='1') ans+=vec[i];
        }
        return ans;
    }
};
