class Solution:
    def maxOperations(self, s: str) -> int:
        res = 0
        n = len(s)


        zeros = 0
        curr = 0
        for i in range(n-1,-1,-1):
            if s[i]=="1":
                if zeros:
                    curr += 1
                res += curr
                zeros = 0
            else:
                zeros += 1

        return res
