class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), cnt = 0, ans = 0;

        for(int i = 0, j = 1; j < n && i < n; ) {
            if(s[i] == '1') {
                ans += cnt;
                j = i + 1;
                while(j < n && s[j] == '1') j ++;
                cnt += j - i;
                i = j;
            } else {
                i ++;
            }
        }

        if(s[n - 1] == '0') ans += cnt;
        return ans;
    }
};
