class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int ans=0;
        int cnt=0;
        for(int i=n-1;i>=0;i--){
            if(s[i]=='0'){
                if(i==n-1||s[i+1]!='0') cnt++;
            }else{
                ans+=cnt;
            }
        }
        return ans;
    }
};
