class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        int res=0,count=0,flag=0;

        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                flag=0;
                count++;
            }
            else if(s[i]=='0' && flag==0){
                flag=1;
                res+=count;
            }
        }
        return res;
    }
};
