class Solution {
public:
    int maxOperations(string s) {
        int total = 0;
        int blocks = 0;
        bool zeroblock = false;
        for (int i = s.length()-1; i >= 0; i--) {
            if (s[i] == '0') {
                if (zeroblock == false)
                    blocks++;
                zeroblock = true;
            } else {
                zeroblock=false;
                total+=blocks;
            }
        }
        return total;
    }
};
