class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        res = zero = 0
        i = n - 1
        while i >= 0:
            j = i
            while j >= 0 and s[j] == '0':
                j -= 1
            if j != i:
                zero += 1
            if j >= 0:
                res += zero
            i = j - 1
        return res
