class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int n=s.size();
        int last=n-1;
        int z=0;
        for(int i=n-1;i>=0;i--)
        {
          if(i==n-1 && s[i]=='0')   
              z++;
            else if(s[i]=='0' && s[i+1]!='0')
                z++;
            else if(s[i]=='1')
            {
                ans+=z;
            }
        }
        return ans;
    }
};
