class Solution {
public:
    int maxOperations(string s) {
        int one = 0, tag = false, res = 0;

        for (auto& c : s) {
            if (c == '0') {
                res += !tag ? one : 0;
                tag = true;
            } else {
                tag = false;
                one++;
            }
        }
        return res;
    }
};
