class Solution {
public:
    int maxOperations(string s) {
        vector<int> ones;

        int count = 0;
        for(auto x:s) {
            if(x == '0') {
                if(count > 0) ones.push_back(count);
                count = 0;
            } else {
                count += 1;
            }
        }

        bool countLast = true;
        if(count > 0) {
            ones.push_back(count);
            countLast = false;
        }

        int ans = 0, nextAdd = 0;
        for(int i = 0; i + 1 < ones.size(); i++) {
            nextAdd += ones[i];
            ans += nextAdd;
        }

        if(countLast && ones.size() > 0) {
            nextAdd += ones.back();
            ans += nextAdd;
        }

        return ans;
    }
};
