class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        long long ans = 0;
        long long c = 0;
        for(int i = n - 2; i >= 0; i--){
            if(s[i] == '1' && s[i + 1] == '0')c++;
            if(s[i] == '1') ans += c;
        }
        return ans;
    }
};
