class Solution:
    def maxOperations(self, s: str) -> int:
        arr = []
        temp = 0
        for c in s:
            if c == '0':
                if temp > 0:
                    arr.append(temp)
                    temp = 0
            else:
                temp += 1

        n = len(arr)
        for i in range(1, n):
            arr[i] += arr[i-1]
        return sum(arr)
