class Solution {
public:
    int maxOperations(string s) {
        s += '1';

        int n = s.size(), res = 0;
        vector<int> idx;
        int i = 0;
        while (i < n) {
            if (s[i] == '0') {
                int j = i;
                while (j < n && s[j] == '0')
                    j ++ ;
                idx.push_back(i);
                i = j;
            } else
                i ++ ;
        }

        int m = idx.size();
        // cout << m << endl;

        for (int i = 0, j = 0; i < n; i ++ ) {
            if (s[i] == '0')
                continue;

            while (j < m && i > idx[j])
                j ++ ;

            if (j < m)
                res += m - j;
        }
        return res;
    }
};
