class Solution {
public:
    int maxOperations(string s) {
        s += '1';
        int a0 = 0, a1 = 0 ,ans = 0;
        for(int i=0;i<s.size();i++) {
            if(s[i]=='1') {
                if(a0) ans += a1, a0 = 0;
                a1++;
            }
            else {
                a0++;
            }
        }   
        return ans;
    }
};
