class Solution {
public:
    int maxOperations(string s) {

        int acc=0;
        int ans=0;
        for(int i=0;i<s.size()-1;i++)
        {
            if(s[i]=='1') acc++;

            if(s[i]=='1' && s[i+1]=='0')
            {
                ans+=acc;
            }
        }

        return ans;
    }
};
