typedef long long ll;

class Solution {
public:
    int maxOperations(string s) {
        vector<int> idxs;
        int n = s.size();
        s.push_back('1');
        int m = -1;
        ll ans = 0;
        for (int i = 0; i <= n; i ++) {
            if (s[i] == '1') {
                idxs.push_back(i);
                m ++;
                if (idxs.size() >= 2) {
                    if (idxs[m - 1] < idxs[m] - 1) {
                        ans += m;
                    }
                }
            }
        }
        return ans;
    }
};
