class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int j = n - 1;
        while (j >= 0 && s[j] == '1') {
            j--;
        }
        int res = 0;
        int now = 0;
        for (int i = 0; i <= j; ++i) {
            if (s[i] == '1') {
                now += 1;
            } else {
                if (i > 0 && s[i-1] == '1') {
                    res += now;
                }
            }
        }
        return res;
    }
};
