class Solution:
    def maxOperations(self, s: str) -> int:
        cnt = 0
        last = -1
        ans = 0
        for i, c in enumerate(s):
            if c == '1':
                ans += cnt * int(i > last + 1)
                last = i
                cnt += 1
        ans += cnt * int(len(s) > last + 1)
        return ans
