class Solution {
public:
    int maxOperations(string s) {
        int cur=0,ans=0;
        for(int i=s.length()-1;i>=0;i--){
            if(s[i]=='0'&&(i==s.length()-1||s[i+1]=='1')){
                cur++;
            }else if(s[i]=='1'){
                ans+=cur;
            }
        }
        return ans;
    }
};
