class Solution {
    public int maxOperations(String s) {

        int n = s.length() ;

        boolean hasZero = false;
        int pre = 0 ;
        int count = 0 ;
        for (int i=0; i<n; i++) {
            char ch = s.charAt(i) ;

            if ( ch == '0') {
                hasZero = true ;
            } else {
                if (hasZero) {
                    count += pre ;
                    hasZero = false;
                } 
                pre ++ ;
            }

        }
        if ( hasZero ) {
            count += pre ;
        }

        return count;

    }
}
