class Solution {
    public int maxOperations(String s) {
        int cnt = 0;
        int sum = 0;
        char[] ch = s.toCharArray();
        int n = ch.length;
        for(int i=0;i<n-1;i++) {
            if(ch[i] == '0') continue;
            if(ch[i+1] == '1') {
                cnt++;
            } else {
                cnt++;
                sum += cnt;
            }
        }
        return sum;
    }
}
