class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int count=0;
        int c=0;
        int n=s.size();
        for(int i=n-1;i>=0 ;i--){
            if(s[i]=='1'){
                if(count){
                    c++;
                    count =0;
                }
              ans+=c;
            }
            else if (s[i]=='0'){
                count++;
            }
        }
        return ans;
    }
};
