class Solution {
    public int maxOperations(String s) {
        // 101010 = 3 + 2 + 1
        // 000110 = 1 + 1
        int count = 0;
        int moves = 0;
        for (int i = s.length() - 2; i >= 0; i--) {
            if (s.charAt(i) == '1') {
                if (s.charAt(i + 1) == '0') {
                    moves++;
                    count += moves;
                } else {
                    count += moves;
                }
            }
        }
        return count;
    }
}
