class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int curCnt=0;
        int ans=0;
        bool zero=false;
        for(int i=0;i<n;i++){
            if(s[i]=='0'){
                zero=true;
            }
            else{
                if(zero){
                    ans+=curCnt;
                }
                curCnt++;
                zero=false;
            }
        }
        if(zero){
            ans+=curCnt;
        }
        return ans;
    }
};
