class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int x=0;
        int index=0;
        while(index<s.length())
        {
            if(s[index]=='1')
            {
                x++;
                index++;
            }     
            else
            {
                while(index<s.length()&&s[index]=='0')
                    index++;
                ans+=x;
            }
        }
        return ans;
    }
};
